package com.ust;

class Switch3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int expression=2;
		
		//switch statement to check size
		switch(expression) {
		case 1:
			System.out.println("Case1");
			
			//matching case
		case 2:
			System.out.println("Case2");
			
		case 3:
			System.out.println("Case3");
			
			default:
				System.out.println("Default case");
		}

	}

}
